import React, { useMemo, useState, useEffect } from "react";
import { useDropzone } from "react-dropzone";
import {
  Box,
  Image,
  IconButton,
  HStack,
  VStack,
  Grid,
  GridItem,
} from "@chakra-ui/react";
import { FiEye, FiTrash2 } from "react-icons/fi";
import { useRecoilState } from "recoil";
import { viewImageState } from "../data";

const baseStyle = {
  flex: 1,
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  padding: "20px",
  borderWidth: 2,
  borderRadius: 2,
  borderColor: "#eeeeee",
  borderStyle: "dashed",
  backgroundColor: "#fafafa",
  color: "#bdbdbd",
  outline: "none",
  transition: "border .24s ease-in-out",
};

const activeStyle = {
  borderColor: "#2196f3",
};

const acceptStyle = {
  borderColor: "#00e676",
};

const rejectStyle = {
  borderColor: "#ff1744",
};

const thumbsContainer = {
  display: "flex",
  flexDirection: "row",
  flexWrap: "wrap",
  marginTop: 16,
};

const thumb = {
  display: "inline-flex",
  borderRadius: 2,
  border: "1px solid #eaeaea",
  marginBottom: 8,
  marginRight: 8,
  width: 100,
  height: 100,
  padding: 4,
  boxSizing: "border-box",
};

const thumbInner = {
  display: "flex",
  minWidth: 0,
  overflow: "hidden",
};

const img = {
  display: "block",
  width: "auto",
  height: "100%",
};

// interface IImageUpload {
// files: File[],
// onDrop: (acceptedFiles: File[]) => void
// }

const ImageUpload = ({ files, setFiles, onDrop }) => {
   const [image, setImage] = useRecoilState(viewImageState);
  const {
    getRootProps,
    getInputProps,
    isDragActive,
    isDragAccept,
    isDragReject,
  } = useDropzone({
    accept: "image/*",
    onDrop: onDrop,
  });

  const style = useMemo(
    () => ({
      ...baseStyle,
      ...(isDragActive ? activeStyle : {}),
      ...(isDragAccept ? acceptStyle : {}),
      ...(isDragReject ? rejectStyle : {}),
    }),
    [isDragActive, isDragAccept, isDragReject]
  );

  const handleDelImage = (filename) => {
    const newfiles = files.filter((r) => r.name != filename);
    setFiles((prev) => (prev = newfiles));
  };

  const handleViewImage = ({ preview, name }) => {
    const newImage = {url: preview, name: name}
    setImage(prev => newImage)

  }

  const thumbs = files.map((file) => (
    // <div style={thumb} key={file.name}>
    //   <div style={thumbInner}>
    //     <img
    //       alt={file.name}
    //       src={file.preview}
    //       style={img}
    //     />
    //   </div>
    // </div>

    <Box
      display="inline-flex"
      w="100%"
      h={150}
      mb={8}
      mr={8}
      p={4}
      border="1px solid #eaeaea"
      br={2}
      key={file.name}
    >
      <HStack p={5}>
        <Image
          src={file.preview}
          alt={file.name}
          display="block"
          w="auto"
          h="100%"
        />
        <VStack>
          <IconButton
            size="sm"
            aria-label="view image"
            icon={<FiEye />}
            onClick={() => handleViewImage({ preview: file.preview, name: file.name })}
          />
          <IconButton
            size="sm"
            aria-label="delete image"
            icon={<FiTrash2 />}
            onClick={() => handleDelImage(file.name)}
          />
        </VStack>
      </HStack>
    </Box>
  ));

  useEffect(
    () => () => {
      // Make sure to revoke the data uris to avoid memory leaks
      files.forEach((file) => URL.revokeObjectURL(file.preview));
    },
    [files]
  );

  return (
    <div className="container">
      <div {...getRootProps({ style })}>
        <input {...getInputProps()} />
        <p>Drag 'n' drop some files here, or click to select files</p>
      </div>
      <aside style={thumbsContainer}>{thumbs}</aside>
    </div>
  );
};

export default ImageUpload;
