import React, { useMemo } from "react";
import MaterialTable, { MTableToolbar } from "material-table";
import { makeStyles } from "@material-ui/core/styles";
import { TextField, Button, MenuItem, Icon } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import CheckIcon from "@material-ui/icons/Check";
import SearchIcon from "@material-ui/icons/Search";
import BuildOutlinedIcon from "@material-ui/icons/BuildOutlined";
//import { useHistory } from "react-router-dom";
//import { useSetRecoilState } from "recoil";
//import { payPeriodEmpIdState } from "./data/atomdata";
import { usePayslipsContext } from "../context/payslips_context";
//import { useEmployeesContext } from "../context/employees_context";

export default function PayslipTableAdmin({
  payslipsdata,
  //setPayslipsdata,
  handleDialogClose,
}) {
  //let history = useHistory();
  const classes = useStyles();
  //const setPayPeriodEmpId = useSetRecoilState(payPeriodEmpIdState);
  const { updatePayrun, batchpayrun, update_payrun_error } =
    usePayslipsContext();

  const columns = useMemo(
    () => [
      {
        title: "Batch",
        field: "payrun",
        editable: "never",
      },
      { title: "Period", field: "period", editable: "never" },
      {
        title: "Pay Date",
        field: "pay_date",
        type: "date",
        dateSetting: { locale: "en-GB" },
        editable: "never",
      },
      {
        title: "Wages",
        field: "totalwages",
        type: "currency",
        editable: "never",
      },
      {
        title: "TAP Amount",
        field: "totaltap",
        type: "currency",
        editable: "never",
      },
      {
        title: "SCP Amount",
        field: "totalscp",
        type: "currency",
        editable: "never",
      },
      {
        title: "Site Allowances",
        field: "totalsitesallows",
        type: "currency",
        editable: "never",
      },
      {
        title: "Expenses Claims",
        field: "totalexpensesclaims",
        type: "currency",
        editable: "never",
      },
      {
        title: "Allowances",
        field: "totalallows",
        type: "currency",
        editable: "never",
      },
      {
        title: "Deductions",
        field: "totaldeducts",
        type: "currency",
        editable: "never",
      },
      {
        title: "Total Payroll",
        field: "totalpayroll",
        type: "currency",
        editable: "never",
      },
      // { title: "Bank Name", field: "bank_name" },
      // { title: "Bank AC No", field: "bank_accno" },
      {
        title: "Status",
        field: "status",
        editable: "never",
        cellStyle: {
          width: 50,
          maxWidth: 50,
        },
        editComponent: (props) => (
          <TextField
            //defaultValue={props.value || null}
            onChange={(e) => props.onChange(e.target.value)}
            style={{ width: 100 }}
            value={props.value}
            select
          >
            <MenuItem value="Pending">Pending</MenuItem>
            <MenuItem value="Approve">Approve</MenuItem>
            <MenuItem value="Reject">Reject</MenuItem>
            <MenuItem value="Cancel">Cancel</MenuItem>
          </TextField>
        ),
      },
    ],
    []
  );

  // const Save_PayslipData = () => {
  //   payslipsdata.forEach((data) => {
  //     const { id } = data;
  //     if (id) {
  //       const { id, rec_id, tableData, ...fields } = data;
  //       updatePayrun({ id, ...fields });
  //     }
  //   });

  //   handleDialogClose();
  // };

  const Approve_PayslipData = () => {
    batchpayrun.forEach((rec) => {
      if (rec.tableData.checked) {
        updatePayrun({ id: rec.id, status: "Approved" });

        if (!update_payrun_error) {
          const recdata = batchpayrun.filter((r) => r.id === rec.id);
          recdata[0].status = "Approved";
        }
      }
    });
    batchpayrun.forEach((d) => {
      if (d.tableData) d.tableData.checked = false;
    });
  };

  const Reject_PayslipData = () => {
    batchpayrun.forEach((rec) => {
      if (rec.tableData.checked) {
        updatePayrun({ id: rec.id, status: "Rejected" });
        //update leavesdata
        if (!update_payrun_error) {
          const recdata = batchpayrun.filter((r) => r.id === rec.id);
          recdata[0].status = "Rejected";
        }
      }
    });
    batchpayrun.forEach((d) => {
      if (d.tableData) d.tableData.checked = false;
    });
  };

  return (
    <div className={classes.root}>
      <div style={{ maxWidth: "100%", paddingTop: "5px" }}>
        <MaterialTable
          columns={columns}
          data={batchpayrun}
          title="Payslips"
          icons={{
            Add: (props) => <AddIcon />,
            Edit: (props) => <EditIcon />,
            Delete: (props) => <DeleteIcon />,
            Clear: (props) => <DeleteIcon />,
            Check: (props) => <CheckIcon />,
            Search: (props) => <SearchIcon />,
            ResetSearch: (props) => <DeleteIcon />,
            Build: (props) => <BuildOutlinedIcon />,
          }}
          // editable={{
          //   onRowUpdate: (newData, oldData) =>
          //     new Promise((resolve, reject) => {
          //       setTimeout(() => {
          //         const dataUpdate = [...payslipsdata];
          //         const index = oldData.tableData.id;
          //         dataUpdate[index] = newData;
          //         setPayslipsdata([...dataUpdate]);
          //         //approve_Expense(newData);

          //         resolve();
          //       }, 1000);
          //     }),
          // }}
          options={{
            filtering: true,
            selection: true,
            exportButton: true,
            headerStyle: {
              backgroundColor: "orange",
              color: "#FFF",
            },
            showTitle: true,
          }}
          components={{
            Toolbar: (props) => (
              <div>
                <MTableToolbar {...props} />
                <div style={{ padding: "5px 10px" }}>
                  <Button
                    type="submit"
                    variant="contained"
                    color="secondary"
                    className={classes.button}
                    onClick={Approve_PayslipData}
                  >
                    Approve <Icon className={classes.rightIcon}>send</Icon>
                  </Button>
                  <Button
                    type="submit"
                    variant="contained"
                    color="secondary"
                    className={classes.button}
                    onClick={Reject_PayslipData}
                  >
                    Reject <Icon className={classes.rightIcon}>send</Icon>
                  </Button>
                  {/* <Button
                    type="submit"
                    variant="contained"
                    color="secondary"
                    className={classes.button}
                    onClick={Save_PayslipData}
                  >
                    Update <Icon className={classes.rightIcon}>send</Icon>
                  </Button> */}
                </div>
              </div>
            ),
          }}
        />
      </div>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
  button: {
    margin: theme.spacing(1),
  },
}));
