              exact
              path="/payroll"
              element={
                <React.Suspense fallback={<>...</>}>
                  <Payroll />
                </React.Suspense>
              }
            />

            <Route
              exact
              path="/payslips"
              element={
                <React.Suspense fallback={<>...</>}>
                  <PayslipStaff />
                </React.Suspense>
              }
            />

            <Route
              exact
              path="/singlepayslip"
              element={
                <React.Suspense fallback={<>...</>}>
                  <SinglePayslip />
                </React.Suspense>
              }
            />

            <Route
              exact
              path="/batchpayslips"
              element={
                <React.Suspense fallback={<>...</>}>
                  <BatchPayslips />
                </React.Suspense>
              }
            />

