          //   open={open}
          //   handleDrawerOpen={handleDrawerOpen}
          //   handleDrawerClose={handleDrawerClose}
          // />
          <Routes>
            <Route exact path="/" element={<Home />}></Route>
            <Route
              exact
              path="/allemployees"
              element={
                <React.Suspense fallback={<>...</>}>
                  <AllEmployees />
                </React.Suspense>
              }
            />
            <Route
              exact
              path="/payroll"
              element={
                <React.Suspense fallback={<>...</>}>
                  <Payroll />
                </React.Suspense>
              }
            />
            <Route
              exact
              path="/payslip"
              element={
                <React.Suspense fallback={<>...</>}>
                  <Payslip />
                </React.Suspense>
              }
            />
            <Route
              exact
              path="/singlepayslip"
              element={
                <React.Suspense fallback={<>...</>}>
                  <SinglePayslip />
                </React.Suspense>
              }
            />
            <Route
              exact
              path="/batchpayslips"
              element={
                <React.Suspense fallback={<>...</>}>
                  <BatchPayslips />
                </React.Suspense>
              }
            />
            <Route
              exact
              path="/leave"
              element={
                <React.Suspense fallback={<>...</>}>
                  <Leave />
                </React.Suspense>
              }
            />
            <Route
              exact
              path="/payrun"
              element={
                <React.Suspense fallback={<>...</>}>
                  <Payrun />
                </React.Suspense>
              }
            />
            <Route
              exact
              path="/payrunbatch"
              element={
                <React.Suspense fallback={<>...</>}>
                  <Payrunbatch />
                </React.Suspense>
              }
            />
            <Route
              exact
              path="/dailyallowances"
              element={
                <React.Suspense fallback={<>...</>}>
                  <DailyAllowances />
                </React.Suspense>
              }
            />
            <Route
              exact
              path="/singledailyallowsdetlstable"
              element={
                <React.Suspense fallback={<>...</>}>
                  <SingleDailyAllowsDetls />
                </React.Suspense>
              }
            />
            <Route exact path="/expenses" element={<Expenses />}></Route>
            <Route exact path="/departments" element={<Departments />}></Route>
            <Route exact path="/designation" element={<Designation />}></Route>
            <Route exact path="/tables" element={<Tables />}></Route>
            <Route exact path="/allowances" element={<Allowances />}></Route>
            <Route exact path="/clients" element={<Clients />}></Route>
            <Route exact path="/example" element={<Example />}></Route>
            <Route
              exact
              path="/singleemployee"
              element={<SingleEmployee />}
            ></Route>
            <Route exact path="/singleleave" element={<SingleLeave />}></Route>
            <Route
              exact
              path="/singleexpense"
              element={<SingleExpense />}
            ></Route>
            <Route
              exact
              path="/singledailyallowance"
              element={<SingleDailyAllowance />}
            ></Route>
            <Route
              exact
              path="/batchdailyallowances"
              element={<BatchDailyAllowances />}
            ></Route>
            <Route exact path="/error" element={<Error />}></Route>
            <Route path="*" element={<Error />}></Route>
          </Routes>
        )}
        {select === "Manager" && (
          // <DashboardManager
          //   open={open}
          //   handleDrawerOpen={handleDrawerOpen}
          //   handleDrawerClose={handleDrawerClose}
          // />
          <Routes>
            <Route exact path="/" element={<Home />}></Route>

            <Route
              exact
              path="/allemployees"
              element={
                <React.Suspense fallback={<>...</>}>
                  <AllEmployees />
                </React.Suspense>
              }
            />
            <Route
              exact
              path="/payroll"
              element={
                <React.Suspense fallback={<>...</>}>
                  <Payroll />
                </React.Suspense>
              }
            />
            <Route
              exact
              path="/payslip"
              element={
                <React.Suspense fallback={<>...</>}>
                  <Payslip />
                </React.Suspense>
              }
            />
            <Route
              exact
              path="/singlepayslip"
              element={<SinglePayslip />}
            ></Route>
            <Route
              exact
              path="/batchpayslips"
              element={
                <React.Suspense fallback={<>...</>}>
                  <BatchPayslips />
                </React.Suspense>
              }
            />
            <Route exact path="/approval" element={<ApprovalManager />}></Route>
            <Route exact path="/leave" element={<Leave />}></Route>
            <Route exact path="/payrun" element={<Payrun />}></Route>
            <Route exact path="/payrunbatch" element={<Payrunbatch />}></Route>
            <Route
              exact
              path="/dailyallowances"
              element={<DailyAllowances />}
            ></Route>
            <Route
              exact
              path="/singledailyallowsdetlstable"
              element={<SingleDailyAllowsDetls />}
            ></Route>
            <Route exact path="/expenses" element={<Expenses />}></Route>
            <Route exact path="/departments" element={<Departments />}></Route>
            <Route
              exact
              path="/useraccess"
              element={<UserAccessTable />}
            ></Route>
            <Route exact path="/designation" element={<Designation />}></Route>
            <Route exact path="/tables" element={<Tables />}></Route>
            <Route exact path="/allowances" element={<Allowances />}></Route>
            <Route exact path="/clients" element={<Clients />}></Route>
            <Route exact path="/example" element={<Example />}></Route>
            <Route
