                            month={10}
                          />
                        </TabPanel>
                        <TabPanel>
                          <HocTableViewSummary
                            year={selecthocyear ? selecthocyear : currentyear}
                            month={11}
                          />
                        </TabPanel>
                        <TabPanel>
                          <HocTableViewSummary
                            year={selecthocyear ? selecthocyear : currentyear}
                            month={12}
                          />
                        </TabPanel>
                      </TabPanels>
                    </Tabs>
                  </Box>
                  <Divider />
                </SimpleGrid>
              </Box>
            </TabPanel>
          </TabPanels>
        </Tabs>
        <Modal
          closeOnOverlayClick={false}
          isOpen={isExport2ExcelOpen}
          onClose={onExport2ExcelClose}
          size="md"
        >
          <ModalOverlay />
          <ModalContent>
            <ModalCloseButton />
            <ModalBody>
              <Export2ExcelDialog
                state={exp2excelstate}
                setState={setExp2excelstate}
                dataset={hoc}
                onClose={onExport2ExcelClose}
              />
            </ModalBody>
          </ModalContent>
        </Modal>
        <CustomDialog
          isOpen={isNewHOCOpen}
          handleClose={onNewHOCClose}
          title=""
          showButton={true}
          isFullscreen={false}
          isFullwidth={false}
          isEditId=""
        >
          <HocForm
            formdata={formdata}
            setFormdata={setFormdata}
            handleDialogClose={onNewHOCClose}
            isNew={true}
          />
        </CustomDialog>
      </Box>
    </Container>
    // </div>
  );
};

