import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import { Navigate } from "react-router-dom";
import {
  BrowserRouter as Router,
  Switch,
  Routes,
  Route,
  Link,
} from "react-router-dom";
import { useRecoilState } from "recoil";
import { Flex } from "@chakra-ui/react";
// import Chart from "./Chart";
// import Deposits from "./Deposits";
// import Orders from "./Orders";

import Appbanner from "./Appbanner";
import Example from "./Example6";
import SideDrawer from "./SideDrawer";
import SingleEmployee from "./SingleEmployee";
import SingleLeave from "./SingleLeave";
import SingleExpense from "./SingleExpense";
import SingleDailyAllowance from "./SingleDailyAllowance";
import SingleDailyAllowsDetls from "./SingleDailyAllowsDetls";
import BatchDailyAllowances from "./BatchDailyAllowances";
import SinglePayslip from "./SinglePayslip";
import { loginLevelState } from "./data/atomdata";
import Login from "./LoginForm";
import Payrun from "./Payrun";
import Payrunbatch from "./Payrunbatch";
import SingleEmployeeStaff from "./SingleEmployeeStaff";
import SingleDailyAllowsDetlsStaff from "./SingleDailyAllowsDetlsStaff";
import ApprovalManager from "./ApprovalManager";
import UserAccessTable from "./UserAccessTable";

import {
  Home,
  AllEmployees,
  Leave,
  DailyAllowancesStaff,
  Expenses,
  Hoc,
  LeaveStaff,
  Payslip,
  BatchPayslips,
  Payroll,
  PayslipStaff,
  TrainingsStaff,
  ExpensesStaff,
  Departments,
  Designation,
  Tables,
  Clients,
  Allowances,
  DailyAllowances,
  PrivateRoute,
  Error,
} from "../pages";

const drawerWidth = 0;

export default function DashboardMain() {
  const classes = useStyles();
  const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
  const [open, setOpen] = React.useState(true);
  const [login, setLogin] = React.useState(true);
  const [select, setSelect] = React.useState("Staff");
  //console.log("loginLevel", loginLevel);

  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleSelect = (title) => {
    setLoginLevel({ ...loginLevel, loginLevel: title });
    setSelect(title);
    <Navigate to="/" />;
  };

  //  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);

  if (!login) {
    return <Login setLogin={setLogin} />;
  }
  return (
    <div className={classes.root}>
      <CssBaseline />
      <Router>
        <Appbanner
          handleDrawerOpen={handleDrawerOpen}
          handleDrawerClose={handleDrawerClose}
          handleSelect={handleSelect}
          open={open}
          setLogin={setLogin}
          title="HRMS V1.5 - AppSmiths Sutera Sdn Bhd"
        />
        <SideDrawer
          handleDrawerOpen={handleDrawerOpen}
          handleDrawerClose={handleDrawerClose}
          open={open}
        />
        <Flex>
          {select === "Staff" && (
            // <DashboardStaff
            //   open={open}
            //   handleDrawerOpen={handleDrawerOpen}
            //   handleDrawerClose={handleDrawerClose}
            // />
            <Routes>
              <Route exact path="/" element={<Home />} />

              <Route exact path="/payroll" element={<Payroll />} />

              <Route exact path="/payslips" element={<PayslipStaff />} />

              <Route exact path="/singlepayslip" element={<SinglePayslip />} />

              <Route exact path="/batchpayslips" element={<BatchPayslips />} />

              <Route exact path="/leave" element={<LeaveStaff />} />

              <Route exact path="/hoc" element={<Hoc />} />

              <Route exact path="/trainings" element={<TrainingsStaff />} />

              <Route
                exact
                path="/dailyallowances"
                element={<DailyAllowancesStaff />}
              />

              <Route
                exact
                path="/singledailyallowsdetlstable"
                element={<SingleDailyAllowsDetlsStaff />}
              ></Route>
              <Route exact path="/expenses" element={<ExpensesStaff />} />

              <Route exact path="/example" element={<Example />}></Route>
              <Route
                exact
                path="/singleemployee"
                element={<SingleEmployeeStaff />}
              ></Route>

              <Route
                exact
                path="/singleleave"
                element={<SingleLeave />}
              ></Route>
              <Route
                exact
                path="/singleexpense"
                element={<SingleExpense />}
              ></Route>
              <Route
                exact
                path="/singledailyallowance"
                element={<SingleDailyAllowance />}
              ></Route>
              <Route
                exact
                path="/batchdailyallowances"
                element={<BatchDailyAllowances />}
              ></Route>
              <Route exact path="/error" element={<Error />} />

              <Route path="*" element={<Error />} />
            </Routes>
          )}
          {select === "Admin" && (
            // <DashboardAdmin
            //   open={open}
            //   handleDrawerOpen={handleDrawerOpen}
            //   handleDrawerClose={handleDrawerClose}
            // />
            <Routes>
              <Route exact path="/" element={<Home />}></Route>
              <Route
                exact
                path="/allemployees"
                element={<AllEmployees />}
              ></Route>

              <Route
                exact
                path="/departments"
                element={<Departments />}
              ></Route>
              <Route
                exact
                path="/designation"
                element={<Designation />}
              ></Route>
              <Route exact path="/tables" element={<Tables />}></Route>
              <Route exact path="/allowances" element={<Allowances />}></Route>
              <Route exact path="/clients" element={<Clients />}></Route>
              <Route exact path="/example" element={<Example />}></Route>
              <Route
                exact
                path="/singleemployee"
                element={<SingleEmployee />}
              ></Route>
              <Route
                exact
                path="/singleleave"
                element={<SingleLeave />}
              ></Route>
              <Route
                exact
                path="/singleexpense"
                element={<SingleExpense />}
              ></Route>
              <Route exact path="/error">
              
              </Route>
              <Route exact path="/leave">
                <Leave />
              </Route>
              <Route path="*">
                <Error />
              </Route>
            </Routes>
          )}
          {select === "OpsSpvr" && (
            // <DashboardOpsSpvr
            //   open={open}
            //   handleDrawerOpen={handleDrawerOpen}
            //   handleDrawerClose={handleDrawerClose}
            // />
            <Routes>
              <Route exact path="/">
                <Home />
              </Route>
              <Route exact path="/allemployees">
                <AllEmployees />
              </Route>

              <Route exact path="/departments">
                <Departments />
              </Route>
              <Route exact path="/designation">
                <Designation />
              </Route>
              <Route exact path="/tables">
                <Tables />
              </Route>
              <Route exact path="/allowances">
                <Allowances />
              </Route>
              <Route exact path="/clients">
                <Clients />
              </Route>
              <Route exact path="/example">
                <Example />
              </Route>
              <Route exact path="/singleemployee">
                <SingleEmployee />
              </Route>
              <Route exact path="/singleleave">
                <SingleLeave />
              </Route>
              <Route exact path="/singleexpense">
                <SingleExpense />
              </Route>
              <Route exact path="/error">
                <Error />
              </Route>
              <Route exact path="/leave">
                <Leave />
              </Route>
              <Route path="*">
                <Error />
              </Route>
            </Routes>
          )}
          {select === "AdminManager" && (
            // <DashboardAdminManager
            //   open={open}
            //   handleDrawerOpen={handleDrawerOpen}
            //   handleDrawerClose={handleDrawerClose}
            // />
            <Routes>
              <Route exact path="/">
                <Home />
              </Route>
              <Route exact path="/allemployees">
                <AllEmployees />
              </Route>
              <Route exact path="/payroll">
                <Payroll />
              </Route>
              <Route exact path="/payslip">
                <Payslip />
              </Route>
              <Route exact path="/singlepayslip">
                <SinglePayslip />
              </Route>
              <Route exact path="/batchpayslips">
                <BatchPayslips />
              </Route>
              <Route exact path="/leave">
                <Leave />
              </Route>
              <Route exact path="/payrun">
                <Payrun />
              </Route>
              <Route exact path="/payrunbatch">
                <Payrunbatch />
              </Route>
              <Route exact path="/dailyallowances">
                <DailyAllowances />
              </Route>
              <Route exact path="/singledailyallowsdetlstable">
                <SingleDailyAllowsDetls />
              </Route>
              <Route exact path="/expenses">
                <Expenses />
              </Route>
              <Route exact path="/departments">
                <Departments />
              </Route>
              <Route exact path="/designation">
                <Designation />
              </Route>
              <Route exact path="/tables">
                <Tables />
              </Route>
              <Route exact path="/allowances">
                <Allowances />
              </Route>
              <Route exact path="/clients">
                <Clients />
              </Route>
              <Route exact path="/example">
                <Example />
              </Route>
              <Route exact path="/singleemployee">
                <SingleEmployee />
              </Route>
              <Route exact path="/singleleave">
                <SingleLeave />
              </Route>
              <Route exact path="/singleexpense">
                <SingleExpense />
              </Route>
              <Route exact path="/singledailyallowance">
                <SingleDailyAllowance />
              </Route>
              <Route exact path="/batchdailyallowances">
                <BatchDailyAllowances />
              </Route>
              <Route exact path="/error">
                <Error />
              </Route>
              <Route path="*">
                <Error />
              </Route>
            </Routes>
          )}
          {select === "Manager" && (
            // <DashboardManager
            //   open={open}
            //   handleDrawerOpen={handleDrawerOpen}
            //   handleDrawerClose={handleDrawerClose}
            // />
            <Routes>
              <Route exact path="/">
                <Home />
              </Route>
              {/* <Route exact path="/home">
              <Home />
            </Route> */}
              <Route exact path="/allemployees">
                <AllEmployees />
              </Route>
              <Route exact path="/payroll">
                <Payroll />
              </Route>
              <Route exact path="/payslip">
                <Payslip />
              </Route>
              <Route exact path="/singlepayslip">
                <SinglePayslip />
              </Route>
              <Route exact path="/batchpayslips">
                <BatchPayslips />
              </Route>
              <Route exact path="/approval">
                <ApprovalManager />
              </Route>
              <Route exact path="/leave">
                <Leave />
              </Route>
              <Route exact path="/payrun">
                <Payrun />
              </Route>
              <Route exact path="/payrunbatch">
                <Payrunbatch />
              </Route>
              <Route exact path="/dailyallowances">
                <DailyAllowances />
              </Route>
              <Route exact path="/singledailyallowsdetlstable">
                <SingleDailyAllowsDetls />
              </Route>
              <Route exact path="/expenses">
                <Expenses />
              </Route>
              <Route exact path="/departments">
                <Departments />
              </Route>
              <Route exact path="/useraccess">
                <UserAccessTable />
              </Route>
              <Route exact path="/designation">
                <Designation />
              </Route>
              <Route exact path="/tables">
                <Tables />
              </Route>
              <Route exact path="/allowances">
                <Allowances />
              </Route>
              <Route exact path="/clients">
                <Clients />
              </Route>
              <Route exact path="/example">
                <Example />
              </Route>
              <Route exact path="/singleemployee">
                <SingleEmployee />
              </Route>
              <Route exact path="/singleleave">
                <SingleLeave />
              </Route>
              <Route exact path="/singleexpense">
                <SingleExpense />
              </Route>
              <Route exact path="/singledailyallowance">
                <SingleDailyAllowance />
              </Route>
              <Route exact path="/batchdailyallowances">
                <BatchDailyAllowances />
              </Route>
              <Route exact path="/error">
                <Error />
              </Route>
              <Route path="*">
                <Error />
              </Route>
            </Routes>
          )}
          {select === "Director" && (
            // <DashboardDirector
            //   open={open}
            //   handleDrawerOpen={handleDrawerOpen}
            //   handleDrawerClose={handleDrawerClose}
            // />
            <Routes>
              <Route exact path="/">
                <Home />
              </Route>
              <Route exact path="/allemployees">
                <AllEmployees />
              </Route>
              <Route exact path="/payroll">
                <Payroll />
              </Route>
              <Route exact path="/payslip">
                <Payslip />
              </Route>
              <Route exact path="/singlepayslip">
                <SinglePayslip />
              </Route>
              <Route exact path="/batchpayslips">
                <BatchPayslips />
              </Route>
              <Route exact path="/approval">
                <ApprovalManager />
              </Route>
              <Route exact path="/leave">
                <Leave />
              </Route>
              <Route exact path="/payrun">
                <Payrun />
              </Route>
              <Route exact path="/payrunbatch">
                <Payrunbatch />
              </Route>
              <Route exact path="/dailyallowances">
                <DailyAllowances />
              </Route>
              <Route exact path="/singledailyallowsdetlstable">
                <SingleDailyAllowsDetls />
              </Route>
              <Route exact path="/expenses">
                <Expenses />
              </Route>
              <Route exact path="/departments">
                <Departments />
              </Route>
              <Route exact path="/useraccess">
                <UserAccessTable />
              </Route>
              <Route exact path="/designation">
                <Designation />
              </Route>
              <Route exact path="/tables">
                <Tables />
              </Route>
              <Route exact path="/allowances">
                <Allowances />
              </Route>
              <Route exact path="/clients">
                <Clients />
              </Route>
              <Route exact path="/example">
                <Example />
              </Route>
              <Route exact path="/singleemployee">
                <SingleEmployee />
              </Route>
              <Route exact path="/singleleave">
                <SingleLeave />
              </Route>
              <Route exact path="/singleexpense">
                <SingleExpense />
              </Route>
              <Route exact path="/singledailyallowance">
                <SingleDailyAllowance />
              </Route>
              <Route exact path="/batchdailyallowances">
                <BatchDailyAllowances />
              </Route>
              <Route exact path="/error">
                <Error />
              </Route>
              <Route path="*">
                <Error />
              </Route>
            </Routes>
          )}
        </Flex>
      </Router>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: "0 8px",
    ...theme.mixins.toolbar,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
