                          />
                        </TabPanel>
                        <TabPanel>
                          <HocTableViewSummary
                            year={selecthocyear ? selecthocyear : currentyear}
                            month={12}
                          />
                        </TabPanel>
                      </TabPanels>
                    </Tabs>
                  </Box>
                  <Divider />
                </SimpleGrid>
              </Box>
            </TabPanel>
            <TabPanel>
              <Box
                maxW="full"
                padding="4"
                width="100%"
                height="700"
                borderColor="blue.500"
                borderWidth="1px"
                borderRadius="lg"
                overflow="scroll"
              >
                <LeavesTableApproval />
              </Box>
            </TabPanel>
          </TabPanels>
        </Tabs>
        <Modal
          closeOnOverlayClick={false}
          isOpen={isExport2ExcelOpen}
          onClose={onExport2ExcelClose}
          size="md"
        >
          <ModalOverlay />
          <ModalContent>
            
            <ModalCloseButton />
            <ModalBody>
              <Export2ExcelDialog
                state={exp2excelstate}
