            <Tabs>
              <TabList>
                <Tab>Employees</Tab>
                <Tab>Leaves</Tab>
                <Tab>HOC History</Tab>
              </TabList>
              <TabPanels>
                <TabPanel>
                  <Box
                    maxW="full"
                    padding="4"
                    width="100%"
                    height="700"
                    borderColor="blue.500"
                    borderWidth="1px"
                    borderRadius="lg"
                    overflow="scroll"
