                      <Box>
                        <Heading as="h2" size="lg">
                          Employees
                        </Heading>
                      </Box>
                      <Divider />
                      <Box>
                        <Tabs isLazy>
                          {/* <TabList>
                            <Tab>Details</Tab>
                          </TabList> */}
                          <TabPanels>
                            <TabPanel>
                              <EmployeeTableLeaveView />
                            </TabPanel>
                          </TabPanels>
                        </Tabs>
                      </Box>
                      <Divider />
                    </SimpleGrid>
                  </Box>
                </TabPanel>
