                                }
                                month={5}
                              />
                            </TabPanel>
                            <TabPanel>
                              <LeavesTableViewSummary
                                year={
                                  selectleaveyear
                                    ? selectleaveyear
                                    : currentyear
                                }
                                month={6}
                              />
                            </TabPanel>
                            <TabPanel>
                              <LeavesTableViewSummary
                                year={
                                  selectleaveyear
                                    ? selectleaveyear
                                    : currentyear
                                }
                                month={7}
                              />
                            </TabPanel>
                            <TabPanel>
                              <LeavesTableViewSummary
                                year={
                                  selectleaveyear
                                    ? selectleaveyear
                                    : currentyear
                                }
                                month={8}
                              />
                            </TabPanel>
                            <TabPanel>
                              <LeavesTableViewSummary
                                year={
                                  selectleaveyear
                                    ? selectleaveyear
                                    : currentyear
                                }
                                month={9}
                              />
                            </TabPanel>
                            <TabPanel>
                              <LeavesTableViewSummary
                                year={
                                  selectleaveyear
                                    ? selectleaveyear
                                    : currentyear
                                }
                                month={10}
                              />
                            </TabPanel>
                            <TabPanel>
                              <LeavesTableViewSummary
                                year={
                                  selectleaveyear
                                    ? selectleaveyear
                                    : currentyear
                                }
                                month={11}
                              />
                            </TabPanel>
                            <TabPanel>
                              <LeavesTableViewSummary
                                year={
                                  selectleaveyear
                                    ? selectleaveyear
                                    : currentyear
                                }
                                month={12}
                              />
                            </TabPanel>
                          </TabPanels>
                        </Tabs>
                      </Box>
                      <Divider />
                    </SimpleGrid>
                  </Box>
                </TabPanel>
                <TabPanel>
                  <Box
                    maxW="100%"
                    padding="4"
                    width="full"
                    height="700"
                    borderColor="blue.500"
                    borderWidth="1px"
                    borderRadius="lg"
                    overflow="scroll"
                  >
                    <SimpleGrid w="168vh">
                      <HStack>
                        <Box>
                          <Heading as="h2" size="lg">
                            HOC History
                          </Heading>
                        </Box>
                        <Box alignSelf="flex-end">
                          <HStack>
                            <Select
                              value={selecthocyear}
                              fontSize={20}
                              onChange={(e) => setSelectHocYear(e.target.value)}
                            >
                              <option value="2021">2021</option>
                              <option value="2022">2022</option>
                            </Select>
                            <Box size="xl" py={2}>
                              <Button
                                colorScheme="teal"
                                variant="solid"
                                onClick={() => handleExportHoc2Excel()}
                              >
                                Export To Excel
                              </Button>
                            </Box>
                          </HStack>
                        </Box>
                      </HStack>
                      <Divider />
                      <Box>
                        <Tabs defaultIndex={currentmonth} isLazy>
                          <TabList>
                            {/* <Tab>Chart</Tab>
                          <Tab>Summary</Tab> */}
                            <Tab>January</Tab>
